import { TEST_DISPATCH, GET_ERRORS, POST_REGISTER } from "./types";
import axios from "axios";

//Register User
export const registerUser = userData => dispatch => {
  axios
    .post("/api/users/register", userData)
    .then(res =>
      dispatch({
        type: POST_REGISTER,
        payload: userData
      })
    )
    .catch(err =>
      dispatch({
        type: GET_ERRORS,
        payload: err.response.data
      })
    );

  return {
    type: TEST_DISPATCH,
    payload: userData
  };
};
