import React, { Component } from "react";
import PostActions from "./PostActions";
import Moment from "react-moment";
import { connect } from "react-redux";
import { deletePost } from "../../actions/postAction";

class Post extends Component {
  state = {
    showPostHeaderSettings: false
  };

  handleDeletePost = e => {
    e.preventDefault();
    console.log("delete");
    const { posts } = this.props;
    const post = posts.map(post => {
      console.log(post._id);
    });

    this.props.deletePost();
  };

  render() {
    const { posts } = this.props;
    //header settings block
    const headerSettings = (
      <div>
        <i
          className="fas fa-trash-alt"
          //delete post
          onClick={this.handleDeletePost}
        />
        <i className="fas fa-marker" />
      </div>
    );

    return (
      <div>
        {posts.map(post => (
          <div key={post._id} className="my-3 ">
            <div className="card ">
              {/*{ Post Header} */}
              <div className="card-header">
                <div className="row">
                  <div className="  col-md-1 col-2">
                    <img
                      src={post.avatar}
                      alt=""
                      style={{ width: "40px" }}
                      className="mr-1 rounded-circle"
                    />
                  </div>
                  <div className=" col-md-10 col-8">
                    <span className="text-info">{post.name} </span>
                    <br />
                    <span className="text-muted">added new message</span>{" "}
                    <Moment fromNow>{post.date}</Moment>
                  </div>
                  <div className=" col-md-1 col ">
                    <i
                      className="fas fa-ellipsis-h "
                      onClick={() => {
                        this.setState({
                          showPostHeaderSettings: !this.state //toggle header settings
                            .showPostHeaderSettings
                        });
                      }}
                    />
                    {this.state.showPostHeaderSettings ? (
                      <div>{headerSettings}</div>
                    ) : null}
                  </div>
                </div>
              </div>

              {/* {end post header} */}

              {/* {Post Body} */}
              <div className="container">
                <div className=" card-body  ">{post.text}</div>
              </div>
              {/* {end Post Body} */}
            </div>
            <PostActions />
          </div>
        ))}
      </div>
    );
  }
}

const mapStateToProps = state => ({
  post: state.post
});

export default connect(
  null,
  { deletePost }
)(Post);
