import React, { Component } from "react";
import { connect } from "react-redux";
import { getPosts } from "../../actions/postAction";
import Spinner from "../layout/Spinner";
import Post from "./Post";

class PostFeed extends Component {
  state = {
    posts: []
  };

  componentDidMount() {
    this.props.getPosts();
  }

  render() {
    const { posts, loading } = this.props.post;
    let feedContent;
    if (posts === [] && loading) {
      feedContent = <Spinner />;
    } else {
      if (posts === []) {
        feedContent = (
          <div className="row">
            <div className="col-md-12">
              <div className="text-center">No posts to show</div>
            </div>
          </div>
        );
      }

      feedContent = <Post posts={posts} />;
    }

    return (
      <div>
        <div className=" my-5">
          <hr />
          <div className="row">
            <div className="col-md-12">
              <h4 className="text-center">Posts Here</h4>
              <ul className="list-group">{feedContent}</ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  post: state.post
});

export default connect(
  mapStateToProps,
  { getPosts }
)(PostFeed);
