import {
  GET_PROFILE,
  PROFILE_LOADING,
  CLEAR_CURRENT_PROFILE,
  UPDATE_CURRENT_PROFILE,
  GET_PROFILES,
  DELETE_PROFILE
} from "../actions/types";

const initialState = {
  profile: null,
  profiles: null,
  loading: false,
  message: null
};

export default function(state = initialState, action) {
  switch (action.type) {
    case PROFILE_LOADING:
      return {
        ...state,
        loading: true
      };
    case GET_PROFILE:
      return {
        ...state,
        profile: action.payload,
        loading: false
      };
    case CLEAR_CURRENT_PROFILE:
      return {
        ...state,
        profile: null,
        loading: false
      };
    case UPDATE_CURRENT_PROFILE:
      return {
        ...state,
        profile: action.payload,
        loading: true
      };
    case GET_PROFILES:
      return {
        ...state,
        profiles: action.payload,
        loading: true
      };
    case DELETE_PROFILE:
      return {
        ...state,
        profile: null,
        loading: true,
        message: action.payload
      };
    default:
      return state;
  }
}
