import React, { Component } from "react";
import Moment from "react-moment";

class Post extends Component {
  render() {
    const { posts } = this.props;
    return (
      <div>
        {posts.map(post => (
          <div key={post._id} className="my-3 ">
            <div className="card card-body">
              {/*{ Post Header} */}
              <div>
                <div className="row">
                  <div className="col">
                    <img
                      src={post.avatar}
                      alt=""
                      style={{ width: "40px" }}
                      className="mr-1 rounded-circle"
                    />
                  </div>
                  <div className="col">
                    <span className="text-info">{post.name} </span>
                    <br />
                    <span className="text-muted">added new message</span>{" "}
                    <Moment fromNow>{post.date}</Moment>
                  </div>
                </div>
              </div>

              {/* {end post header} */}
              {post.text}
            </div>
          </div>
        ))}
      </div>
    );
  }
}
export default Post;
