import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import TextInputForm from "../common/TextInputForm";
import TextAreaForm from "../common/TextAreaForm";
import SelectInputForm from "../common/SelectInputForm";
import InputSocilaForm from "../common/InputSocilaForm";
import PropTypes from "prop-types";
import isEmpty from "../../validation/is_Empty";
import Spinner from "../layout/Spinner";
import {
  registerCurrentProfile,
  getCurrentProfile
} from "../../actions/profileAction";

class EditProfile extends Component {
  constructor(props) {
    super(props);

    //create refs
    this.handleInput = React.createRef();
    this.statusInput = React.createRef();
    this.skillsInput = React.createRef();
    this.companyInput = React.createRef();
    this.websiteInput = React.createRef();
    this.locationInput = React.createRef();
    this.githubusernameInput = React.createRef();
    this.bioInput = React.createRef();
    this.SkillsInput = React.createRef();
    this.SkillsInput = React.createRef();

    this.state = {
      displaySocialInput: false,
      handle: "",
      company: "",
      website: "",
      location: "",
      status: "",
      skills: "",
      githubusername: "",
      bio: "",
      twitter: "",
      facebook: "",
      linkedin: "",
      youtube: "",
      instagram: "",
      errors: {},
      user: {}
    };
    this.onChange = this.onChange.bind(this);
  }

  onChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  componentDidMount() {
    this.props.getCurrentProfile();
  }

  static getDerivedStateFromProps(props, state) {
    //bring in user from props

    const { user } = props.auth;
    if (user) {
      return {
        user: props.auth.user,
        errors: props.errors
      };
    }
  }

  // Submit form

  onSubmit = e => {
    const newProfile = {};
    e.preventDefault();
    if (this.props.profile.profile) {
      const profile = this.props.profile.profile;

      if (isEmpty(this.handleInput.current.value)) {
        newProfile.handle = "";
      } else {
        newProfile.handle = this.handleInput.current.value;
      }

      if (isEmpty(this.skillsInput.current.value)) {
        newProfile.skills = "";
      } else {
        newProfile.skills = this.skillsInput.current.value;
      }

      if (isEmpty(this.statusInput.current.value)) {
        newProfile.status = "";
      } else {
        newProfile.status = this.statusInput.current.value;
      }

      newProfile.company = "";
    }

    // const newProfile = {
    //   handle,
    //   company: this.companyInput.current.value,
    //   website: this.websiteInput.current.value,
    //   location: this.locationInput.current.value,
    //   status: this.statusInput.current.value,
    //   skills: this.skillsInput.current.value,
    //   githubusername: this.githubusernameInput.current.value,
    //   bio: this.bioInput.current.value,
    //   twitter,
    //   facebook,
    //   linkedin,
    //   youtube,
    //   instagram
    // };
    this.props.registerCurrentProfile(newProfile, this.props.history);
    console.log(newProfile);
  };

  render() {
    const { displaySocialInput, errors, user } = this.state;

    //Select options for status
    const options = [
      { label: "* Select Professional status", value: 0 },
      { label: " Developer", value: "Developer" },
      { label: " Junior Developer", value: "Junior Developer" },
      { label: " Senior Developer", value: "Senior Developer" },
      { label: " Manager", value: "Manager" },
      { label: " Student or Lernining", value: "Student or Lernining" },
      { label: " Instructor or Teacher", value: "Instructor or Teacher" },
      { label: " Intern", value: "Intern" },
      { label: "Other", value: "Other" }
    ];

    const { profile } = this.props.profile;
    //if there profile

    if (profile) {
      return (
        <div>
          <div className="container">
            <div className="row">
              <div className="col-md-12 mx-auto">
                <div className="display-4 text-center">Edit Profile</div>
                <p>Dear {user.name} ,you can edit your profile here.</p>
                <small className=" d-block pb-3">* = required fields</small>
                <div className="card">
                  <div className="card-body">
                    <form onSubmit={this.onSubmit}>
                      <TextInputForm
                        name="handle"
                        defaultValue={profile.handle}
                        placeholder=" * Handle"
                        refer={this.handleInput}
                        error={errors.handle}
                        info="A unique handle for your profile URL.Your full name, company name, nick name."
                      />
                      <SelectInputForm
                        name="status"
                        defaultValue={profile.status}
                        refer={this.statusInput}
                        placeholder="Status"
                        options={options}
                        error={errors.status}
                      />
                      <TextInputForm
                        name="skills"
                        defaultValue={profile.skills}
                        placeholder=" * Skills"
                        refer={this.skillsInput}
                        error={errors.handle}
                        info="Please use comma separated values (eg. HTML,CSS,PHP,JavaScrypt)"
                      />
                      <TextInputForm
                        name="company"
                        defaultValue={profile.company}
                        placeholder=" Company"
                        refer={this.companyInput}
                        error={errors.company}
                        info="Coudld be your own company or one you work for"
                      />
                      <TextInputForm
                        name="website"
                        defaultValue={profile.website}
                        placeholder=" Website"
                        refer={this.websiteInput}
                        error={errors.website}
                        info="Coudld be your own company website"
                      />
                      <TextInputForm
                        name="location"
                        defaultValue={profile.location}
                        placeholder="Location"
                        refer={this.locationInput}
                        error={errors.location}
                        info="City & State"
                      />
                      <TextInputForm
                        name="githubusername"
                        defaultValue={profile.githubusername}
                        placeholder="GitHub Username"
                        refer={this.githubusernameInput}
                        error={errors.githubusername}
                        info="If you want your latest repos and aGithub link, include your username"
                      />
                      <TextAreaForm
                        name="bio"
                        defaultValue={profile.bio}
                        placeholder="Short Bio"
                        refer={this.bioInput}
                        error={errors.bio}
                        info="Tell us a little about yourself"
                      />
                      <div className="mb-3">
                        <button
                          type="button"
                          className="btn btn-light mt-3"
                          onClick={() => {
                            this.setState({
                              displaySocialInput: !this.state.displaySocialInput
                            });
                          }}
                        >
                          Add Social Network Links
                        </button>{" "}
                      </div>
                      {displaySocialInput ? (
                        <div>
                          <InputSocilaForm
                            name="twitter"
                            defaultValue={profile.twitter}
                            placeholder="Twitter Profile URL"
                            error={errors.twitter}
                            icon="fab fa-twitter"
                            onChange={this.onChange}
                          />
                          <InputSocilaForm
                            name="facebook"
                            defaultValue={profile.facebook}
                            placeholder="Facebook Profile URL"
                            error={errors.facebook}
                            icon="fab fa-facebook"
                            onChange={this.onChange}
                          />
                          <InputSocilaForm
                            name="linkedin"
                            defaultValue={profile.linkedin}
                            placeholder="Linkedin Profile URL"
                            error={errors.linkedin}
                            icon="fab fa-linkedin"
                            onChange={this.onChange}
                          />
                          <InputSocilaForm
                            name="youtube"
                            defaultValue={profile.youtube}
                            placeholder="Youtube Channel URL"
                            error={errors.youtube}
                            icon="fab fa-youtube"
                            onChange={this.onChange}
                          />
                          <InputSocilaForm
                            name="instagram"
                            defaultValue={profile.instagram}
                            placeholder="Instagram Profile URL "
                            error={errors.instagram}
                            icon="fab fa-instagram"
                            onChange={this.onChange}
                          />
                        </div>
                      ) : null}

                      <input
                        type="submit"
                        value="Submit"
                        className="btn btn-lg btn-info mt-3"
                      />
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      );
    } else {
      return <Spinner />;
    }
  }
}
EditProfile.propTypes = {
  auth: PropTypes.object.isRequired,
  profile: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors,
  profile: state.profile,
  registerCurrentProfile: PropTypes.func.isRequred,
  getCurrentProfile: PropTypes.func.isRequred
});

export default connect(
  mapStateToProps,
  { registerCurrentProfile, getCurrentProfile }
)(withRouter(EditProfile));
