import { TEST_DISPATCH, GET_ERRORS } from "./types";
import axios from "axios";

//Register User
export const registerUser = userData => dispatch => {
  axios.post("/api/users/register", userData).then(res =>
    console.log(res.data).catch((
      err //using thunk
    ) =>
      dispatch({
        type: GET_ERRORS,
        payload: err.res.data
      })
    )
  );

  return {
    type: TEST_DISPATCH,
    payload: userData
  };
};
